from flask import Blueprint, jsonify, request
from flask_jwt_extended import create_access_token, get_jwt_identity, jwt_required
import re
from app import db
from app.models import Usuario, TipoUsuario, Historial, Preferencia, Delegacion, Colonia, Etiqueta, TipoSitio, EtiquetaTipoSitio


bp = Blueprint('usuario_routes', __name__)

@bp.route('/login', methods=['POST'])
def inicio_sesion():
    data = request.get_json()

    correo = data.get('correo')
    contrasena = data.get('contrasena')

    if not correo or not contrasena:
        return jsonify({"error": "Correo y contraseña requeridos"}), 400

    usuario = Usuario.query.filter_by(correo_usuario=correo).first()

    if usuario is None:
        return jsonify({"error": "Correo no encontrado"}), 404

    if usuario.contrasena != contrasena:
        return jsonify({"error": "Contraseña incorrecta"}), 401
    
    access_token = create_access_token(identity=usuario.correo_usuario)
    
    tipo_usuario = TipoUsuario.query.filter_by(cve_tipo_usuario=usuario.cve_tipo_usuario).first()
    
    if tipo_usuario.tipo_usuario == 'Administrador':
        return jsonify({"access_token": access_token, "usuario": usuario.usuario, "tipo_usuario": "administrador"}), 200

    return jsonify({"access_token": access_token, "usuario": usuario.usuario, "tipo_usuario": "registrado"}), 200

@bp.route('/registro', methods=['POST'])
def registrar_usuario():
    data = request.get_json()

    correo = data.get('correo')
    usuario = data.get('usuario')
    contrasena = data.get('contrasena')
    foto_usuario = data.get('foto_usuario')

    if not correo or not usuario or not contrasena:
        return jsonify({"error": "Hacen falta datos."}), 400
    
    existe_correo = Usuario.query.filter_by(correo_usuario=correo).first()
    
    correo_regex = r'^[\w\.-]+@[\w\.-]+\.\w+$'
    if not re.match(correo_regex, correo):
        return jsonify({"error": "El correo ingresado no tiene el formato adecuado."}), 400

    contrasena_regex = r'^(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*()_+])[A-Za-z\d!@#$%^&*()_+]{8,}$'
    if not re.match(contrasena_regex, contrasena):
        return jsonify({"error": "La contraseña debe contener al menos 8 caracteres, una letra mayúscula, un número y un carácter especial."}), 400

    if existe_correo is not None:
        return jsonify({"error": "Ya existe el correo ingresado."}), 404
    
    existe_usuario = Usuario.query.filter_by(usuario=usuario).first()

    if existe_usuario is not None:
        return jsonify({"error": "Ya existe el usuario ingresado."}), 404

    tipo_usuario = TipoUsuario.query.filter_by(tipo_usuario="Usuario registrado").first()    
    
    nuevo_usuario = Usuario(
        correo_usuario=correo, 
        usuario=usuario, 
        contrasena=contrasena, 
        foto_usuario=foto_usuario, 
        cve_tipo_usuario=tipo_usuario.cve_tipo_usuario,
        habilitado=True
    )
    db.session.add(nuevo_usuario)
    db.session.commit()
    return jsonify({"usuario": usuario.usuario, "message": "Usuario creado con éxito"}), 201

@bp.route('/user', methods=['GET'])
@jwt_required()
def obtener_usuario():
    
    user_id = get_jwt_identity()
    print(f"user_id: {user_id}")  # Agrega esta línea para imprimir el user_id
    usuario = Usuario.query.get(user_id)

    if not usuario:
        print("Usuario no encontrado")
        return {"error": "Usuario no encontrado"}, 404
    
    tipo_usuario = TipoUsuario.query.filter_by(cve_tipo_usuario=usuario.cve_tipo_usuario).first() 

    # Obtener el historial del usuario
    historial = Historial.query.filter_by(cve_usuario=usuario.correo_usuario).all()

    # Obtener las preferencias del usuario
    preferencias = Preferencia.query.filter_by(correo_usuario=usuario.correo_usuario).all()

    # Realizar consultas adicionales para buscar sitios que tengan las mismas preferencias que el usuario
    # (aquí tendrás que implementar la lógica adecuada según tu modelo de datos y relaciones)

    # Devolver los datos del usuario y otros datos relacionados
    return jsonify({
        "usuario": {
            "usuario": usuario.usuario,
            "correo": usuario.correo_usuario,
            "tipo_usuario": tipo_usuario.tipo_usuario,
        },
        "historial": [h.to_dict() for h in historial],
        "preferencias": [p.to_dict() for p in preferencias],
    })

